mapper.select:
SELECT count(*) FROM( SELECT tsf.supplier_id, tsf.company_id, count(*) FROM t_supplier_files tsf
inner join ( select * from( select tscr.supplier_id, tscr.check_score from t_supplier_checkfactory_report tscr order by tscr.create_dt DESC ) as newtable GROUP BY supplier_id ) as cs on cs.supplier_id=tsf.supplier_id
and cs.check_score >= #{minScore}
and cs.check_score <= #{maxScore}
and tsf.no_check = 0
inner join t_supplier_tag_info tsti on tsti.supplier_id = tsf.supplier_id and tsti.tag_id = #{tagId}
inner join t_app_company_info as taci on taci.company_id = tsf.company_id and taci.apply_sts = 15
and( taci.corporation LIKE CONCAT('%','${complexKey}','%') OR taci.m_phone LIKE CONCAT('%','${complexKey}','%') OR taci.contact_addr LIKE CONCAT('%','${complexKey}','%') OR tsf.supplier_cpyname LIKE CONCAT('%','${complexKey}','%') )
inner join (SELECT * FROM (SELECT * FROM t_admittance_invite ORDER BY create_dt DESC ) as newtable GROUP BY accepter_id,supplier_id )as tai on tai.supplier_id = tsf.supplier_id and tai.accepter_id = tsf.company_id
inner join t_access_application_category taac on taac.record_id = tsf.record_id
inner join t_access_application_category taac on taac.record_id = tsf.record_id inner join t_purchase_category tpc on tpc.category_id = taac.category_id and tpc.purchase_nature_id = #{purchaseNature}
left join t_supplier_owner_info as soi on soi.supplier_id = tsf.supplier_id left join t_supplier_regaccount_info tsri on tsri.supplier_id = tsf.supplier_id WHERE tsri.reg_id = #{accountId} and tsf.file_status = #{fileStatus} and tsf.is_delete = 0
and tai.invite_status = 0 and DATE_ADD( tai.create_dt,INTERVAL 7 DAY) >= now() and tai.invite_status = 1 and tai.invite_status = 0 and DATE_ADD( tai.create_dt,INTERVAL 7 DAY) < now()
and(
FLOOR(taci.contact_addr_code / 10000)*10000 in
#{item}
FLOOR(taci.contact_addr_code / 100)*100 in
#{item}
(FLOOR(taci.contact_addr_code / 10000)*10000 in
#{item}
)or( FLOOR(taci.contact_addr_code / 100)*100 in
#{item}
)
)
and taac.category_id in
#{item}
GROUP BY tsf.supplier_id ) as newTable

select * from ( SELECT * from( SELECT tsf.supplier_id, tsf.company_id, tsf.record_id, tsf.supplier_cpyname, tsf.file_status, tsf.create_dt, tsf.no_check, tsf.is_auth_updated, tsf.auth_dt, tsf.source_type, soi.company_id owner_id, tsoo.create_dt exampt_dt, taci.corporation,
(SELECT content from t_access_application_info taai1 where e_id = 14 and tsf.supplier_id = taai1.supplier_id) as m_phone, (SELECT content from t_access_application_info taai2 where e_id = 52 and tsf.supplier_id = taai2.supplier_id) as contact_addr_code, (SELECT content from t_access_application_info taai3 where e_id = 13 and tsf.supplier_id = taai3.supplier_id) as contact_addr, tcah.created_dt authchange_dt FROM t_supplier_files tsf left join (SELECT * FROM (SELECT * FROM t_company_authcation_history ORDER BY created_dt DESC ) as newtable GROUP BY company_id )as tcah on tcah.company_id = tsf.company_id and DATE_ADD(tcah.created_dt,INTERVAL 30 DAY) > now()
inner join ( select * from( select tscr.supplier_id, tscr.check_score from t_supplier_checkfactory_report tscr order by tscr.create_dt DESC ) as newtable GROUP BY supplier_id ) as cs on cs.supplier_id=tsf.supplier_id
and cs.check_score >= #{minScore}
and cs.check_score <= #{maxScore}
and tsf.no_check = 0
inner join t_supplier_tag_info tsti on tsti.supplier_id = tsf.supplier_id and tsti.tag_id = #{tagId}
inner join t_app_company_info as taci on taci.company_id = tsf.company_id and taci.apply_sts = 15
and( taci.corporation LIKE CONCAT('%','${complexKey}','%') OR taci.m_phone LIKE CONCAT('%','${complexKey}','%') OR taci.contact_addr LIKE CONCAT('%','${complexKey}','%') OR tsf.supplier_cpyname LIKE CONCAT('%','${complexKey}','%') )
inner join (SELECT * FROM (SELECT * FROM t_admittance_invite ORDER BY create_dt DESC ) as newtable GROUP BY accepter_id,supplier_id )as tai on tai.supplier_id = tsf.supplier_id and tai.accepter_id = tsf.company_id
inner join t_supplier_out_oprate tsoo on tsoo.supplier_id = tsf.supplier_id
inner join t_access_application_category taac on taac.record_id = tsf.record_id
inner join t_access_application_category taac on taac.record_id = tsf.record_id inner join t_purchase_category tpc on tpc.category_id = taac.category_id and tpc.purchase_nature_id = #{purchaseNature}
left join t_supplier_owner_info as soi on soi.supplier_id = tsf.supplier_id left join t_supplier_regaccount_info tsri on tsri.supplier_id = tsf.supplier_id WHERE tsri.reg_id = #{accountId} and tsf.file_status = #{fileStatus} and tsf.is_delete = 0
and tai.invite_status = 0 and DATE_ADD( tai.create_dt,INTERVAL 7 DAY) >= now() and tai.invite_status = 1 and tai.invite_status = 0 and DATE_ADD( tai.create_dt,INTERVAL 7 DAY) < now()
and(
FLOOR(taci.contact_addr_code / 10000)*10000 in
#{item}
FLOOR(taci.contact_addr_code / 100)*100 in
#{item}
(FLOOR(taci.contact_addr_code / 10000)*10000 in
#{item}
)or( FLOOR(taci.contact_addr_code / 100)*100 in
#{item}
)
)
and taac.category_id in
#{item}
ORDER BY FIELD(owner_id,#{companyId}) DESC ) as newtable GROUP BY supplier_id
ORDER BY FIELD(owner_id,#{companyId}) DESC,auth_dt DESC
ORDER BY FIELD(owner_id,#{companyId}) DESC,create_dt DESC
ORDER BY FIELD(owner_id,#{companyId}) DESC,exampt_dt DESC
) as newtable2 limit #{start},#{limit}

