mapper.update:
update
	t_supplier_files
set
	file_status = #{fileStatus}
where
	supplier_id = #{supplierId}

mapper.select:
select * from t_supplier_files where 1=1 and supplier_id IN (select supplier_id from t_supplier_regaccount_info where 1=1
and reg_id=#{reg_id}
and reg_id=(select reg_id from t_app_sub_account where sa_id=#{sa_id} limit 1)
) and company_id=#{company_id} limit 1

select tsf.supplier_id, tsf.company_id, tsf.record_id, tsf.supplier_cpyname, tsf.file_status, tsf.create_dt, tsf.no_check, tsf.no_checkor_id, tsf.no_checkor, tsf.no_check_dt, tsf.is_auth_updated, tsf.auth_dt, tsf.source_type, tsf.is_delete from t_supplier_files tsf, t_supplier_owner_info tsoi,
t_supplier_regaccount_info tsri,
t_subaccount_supplier_info tssi,
t_access_application_record taar, t_app_company_info taci where tsf.no_check = 1 and taci.company_id = tsf.company_id
and tsf.supplier_id = tsri.supplier_id and tsri.reg_id = #{regId}
and tsf.supplier_id = tssi.supplier_id and tssi.is_delete = 0 and tssi.sa_id = #{saId}
and tsoi.supplier_id = tsf.supplier_id and tsoi.company_id = #{companyId} and taar.record_id = tsf.record_id and taar.access_status = 3
AND( tsf.supplier_cpyname like CONCAT("%",#{mulParams},"%") or tsf.no_checkor like CONCAT("%",#{mulParams},"%") )
and tsf.no_check_dt >= #{startDate} and tsf.no_check_dt <= #{endDate}
and(
FLOOR(taci.contact_addr_code / 10000)*10000 in
#{item}
FLOOR(taci.contact_addr_code / 100)*100 in
#{item}
(FLOOR(taci.contact_addr_code / 10000)*10000 in
#{item}
)or( FLOOR(taci.contact_addr_code / 100)*100 in
#{item}
)
)
order by tsf.no_check_dt desc limit #{start},#{limit}

select count(*) from t_supplier_files tsf, t_supplier_owner_info tsoi,
t_supplier_regaccount_info tsri,
t_subaccount_supplier_info tssi,
t_access_application_record taar, t_app_company_info taci where tsf.no_check = 1 and taci.company_id = tsf.company_id
and tsf.supplier_id = tsri.supplier_id and tsri.reg_id = #{regId}
and tsf.supplier_id = tssi.supplier_id and tssi.is_delete = 0 and tssi.sa_id = #{saId}
and tsoi.supplier_id = tsf.supplier_id and tsoi.company_id = #{companyId} and taar.record_id = tsf.record_id and taar.access_status = 3
AND( tsf.supplier_cpyname like CONCAT("%",#{mulParams},"%") or tsf.no_checkor like CONCAT("%",#{mulParams},"%") )
and tsf.no_check_dt >= #{startDate} and tsf.no_check_dt <= #{endDate}
and(
FLOOR(taci.contact_addr_code / 10000)*10000 in
#{item}
FLOOR(taci.contact_addr_code / 100)*100 in
#{item}
(FLOOR(taci.contact_addr_code / 10000)*10000 in
#{item}
)or( FLOOR(taci.contact_addr_code / 100)*100 in
#{item}
)
)

mapper.update:
update
	t_supplier_files
set
	supplier_cpyname = #{cpName}
where
	supplier_id = #{supplierId}

mapper.select:
SELECT tsf.supplier_id, tsf.company_id, tsf.record_id, tsf.supplier_cpyname, tsf.file_status, tsf.create_dt, tsf.no_check, tsf.is_auth_updated, tsf.auth_dt, tsf.source_type FROM
t_supplier_regaccount_info tsri,
t_subaccount_supplier_info tssi,
t_supplier_files tsf, t_supplier_owner_info tsoi WHERE tsoi.supplier_id = tsf.supplier_id and tsoi.company_id = #{companyId}
AND tsri.supplier_id = tsf.supplier_id AND tsri.reg_id = #{regId}
AND tssi.supplier_id = tsf.supplier_id AND tssi.is_delete = 0 AND tssi.sa_id = #{saId}
and tsf.is_delete = 0 and tsf.file_status = 0
